import { useState } from 'react'

const Heading = ({text}) => <h1>{text}</h1>

const Button = ({text, onClick}) => <button onClick={onClick}>{text}</button>

const StatNum = ({text, total}) => <div>{text}: {Math.round(total * 100) / 100}</div>

const StatTitle = ({text}) => <div>{text}</div>;

const Statistics = ({totals}) => {
  if (totals[0] + totals[1] + totals[2] === 0) return (
    <div>
      <Heading text='Statistics' />
      <p>No feedback given yet. Try adding some!</p>
    </div>
  )

  return (
    <div>
      <Heading text='Statistics' />
      <table>
        <tr>

        </tr>
      </table>
      <StatNum text='Good' total={totals[0]} />
      <StatNum text='Neutral' total={totals[1]} />
      <StatNum text='Bad' total={totals[2]} />
      <StatNum text='All' total={totals[0] + totals[1] + totals[2]} />
      <StatNum text='Average' total={(totals[0] - totals[2]) / (totals[0] + totals[1] + totals[2])} />
      <StatNum text='Positive' total={totals[0] / (totals[0] + totals[1] + totals[2])} />
    </div>
  )
}

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const addGood = () => setGood(good + 1)
  const addNeutral = () => setNeutral(neutral + 1)
  const addBad = () => setBad(bad + 1)

  return (
    <div>
      <Heading text='Give Feedback' />
      <Button text='Good' onClick={addGood} />
      <Button text='Neutral' onClick={addNeutral} />
      <Button text='Bad' onClick={addBad} />

      <Statistics totals={[good, neutral, bad]}/>
    </div>
  )
}

export default App
